<?php 	function confirm_query($result_set){		if (!$result_set) {		die("Database Query faild.");		}	}function form_errors($error = array()){	$output="";	if(!empty($error)){	$output = "<div class=\"error\">";	$output .= "لطفا مشکل را برطرف کنید:";	$output .= "<ul>";	foreach ($error as $key => $values) {		$output .= "<li>";		$output .= htmlentities($values);		$output .= "</li>";	}	$output .= "</ul>". "</div>";}	return $output;}	function redirect_to($new_location){		header("location:" .$new_location);		exit;	}	function mysql_prep($string){		global $Connection;		$escape_string = mysqli_real_escape_string($Connection, $string);		return $escape_string;	}	function select_all_subjects($public = true){		global  $Connection;		$query  = "SELECT * "; 		$query .= "FROM subject ";		if ($public) {		$query .= "WHERE visible = 1 ";		}		$query .= "ORDER BY position";		$subject_set=mysqli_query($Connection, $query);		confirm_query($subject_set);		return $subject_set;	}	function find_pages_for_subject($subject_id, $public=true){		global $Connection;		$safe_subject_id = mysqli_real_escape_string($Connection,$subject_id);		$query  = "SELECT * "; 		$query .= "FROM pages ";		$query .= "WHERE subject_id= {$safe_subject_id} ";		if ($public) {		$query .= "AND visible = 1 ";		}		$query .= "ORDER BY position ASC"; 		$page_set=mysqli_query($Connection, $query);		confirm_query($page_set);		return $page_set;	}	function find_all_admins(){		global  $Connection;				$query  = "SELECT * "; 		$query .= "FROM admins ";		$query .= "ORDER BY username ASC";		$admin_set=mysqli_query($Connection, $query);		confirm_query($admin_set);		return $admin_set;	}	function find_subject_by_id($subject_id , $public = true){		global  $Connection;		$safe_subject_id = mysqli_real_escape_string($Connection,$subject_id);		$query  = "SELECT * "; 		$query .= "FROM subject ";		$query .= "WHERE id = {$safe_subject_id} ";		if ($public) {			$query .= "AND visible = 1 ";		}		$query .= "LIMIT 1";		$subject_set=mysqli_query($Connection, $query);		confirm_query($subject_set);		if ($subject=mysqli_fetch_assoc($subject_set)) {			return $subject;		} else {			return null;		}	}	function find_page_by_id($page_id , $public=true){			global  $Connection;		$safe_page_id = mysqli_real_escape_string($Connection, $page_id);		$query  = "SELECT * "; 		$query .= "FROM pages ";		$query .= "WHERE id = {$safe_page_id} ";		if ($public) {			$query .= "AND visible = 1 ";		}		$query .= "LIMIT 1";		$page_set=mysqli_query($Connection, $query);		confirm_query($page_set);		if ($page=mysqli_fetch_assoc($page_set)) {			return $page;		} else {			return null;		}	}	function find_admin_by_id($admin_id){		global  $Connection;		$safe_admin_id = mysqli_real_escape_string($Connection,$admin_id);		$query  = "SELECT * "; 		$query .= "FROM admins ";		$query .= "WHERE id = {$safe_admin_id} ";		$query .= "LIMIT 1";		$admin_set = mysqli_query($Connection, $query);		//echo mysqli_error($admin_set); exit;		confirm_query($admin_set);		if ($admin=mysqli_fetch_assoc($admin_set)) {			return $admin;		} else {			return null;		}	}	function find_admin_by_user($username){		global  $Connection;		$safe_username = mysqli_real_escape_string($Connection, $username);		$query  = "SELECT * "; 		$query .= "FROM admins ";		$query .= "WHERE username = '{$safe_username}' ";		$query .= "LIMIT 1 ";		$admin_set=mysqli_query($Connection, $query);     		confirm_query($admin_set);		if ($admin = mysqli_fetch_assoc($admin_set)) {			return $admin;		} else {			return null;		}	}	function find_defult_page_for_subject($subject_id) {			$page_set = find_pages_for_subject($subject_id);		if ($first_page= mysqli_fetch_assoc($page_set)) {			return $first_page;		} else{			return null;		}}	function find_selected_page($public=false) {			global $current_subject;		global $current_page;			if (isset($_GET["subject"])) {		$current_subject = find_subject_by_id($_GET["subject"], $public);		if ($current_subject && $public) {			$current_page = find_defult_page_for_subject($current_subject["id"]);		}else{			$current_page = null;		}			}elseif (isset($_GET["page"])) {		//$selected_subject_id = null;		$current_subject= null;		$current_page =find_page_by_id($_GET["page"], $public);	} else {		$current_subject = null;		$current_page = null;			}}	// Navigation takes 2 arguments	// - the currently selected subject ID (if ay)	// - the currently selected page ID (if ay)		function navigation($subject_array,$page_array){		$output = "<ul class=\"subjects\">";		$subject_set=select_all_subjects(false);		while ($subject = mysqli_fetch_assoc($subject_set)) {		$output .= "<li ";		if ($subject_array && $subject["id"]== $subject_array["id"]) {		 	$output .= "class=\"selected\"";		 } 		$output .= ">";		$output .= "<a href=\"manage_content.php?subject=";		$output .= urldecode($subject["id"]); 		$output .= "\">"; 		$output .= htmlentities($subject["menu_name"]); 		$output .= "</a>";				$output .= "<ul class=\"pages\">";		$page_set=find_pages_for_subject($subject["id"], false);		while ($page = mysqli_fetch_assoc($page_set)) {			$output .= "<li ";			if ($page_array && $page["id"] == $page_array["id"]) {			$output .= "class=\"selected\"";		}		$output .= ">";		$output .= "<a href=\"manage_content.php?page=";		$output .= urldecode($page["id"]);		$output .= "\">";		$output .= htmlentities($page["menu_name"]);		$output .= "</a>";	}		mysqli_free_result($page_set);		$output .= "</li> </ul> </li>";	}		mysqli_free_result($subject_set);		$output .= "</ul>";		return $output;		}	function public_navigation($subject_array,$page_array){		$output = "<ul class=\"subjects\">";		$subject_set=select_all_subjects();		while ($subject = mysqli_fetch_assoc($subject_set)) {		$output .= "<li ";		if ($subject_array && $subject["id"]== $subject_array["id"]) {		 	$output .= "class=\"selected\"";		 } 		$output .= ">";		$output .= "<a href=\"index.php?subject=";		$output .= urldecode($subject["id"]); 		$output .= "\">"; 		$output .= htmlentities($subject["menu_name"]); 		$output .= "</a>";				if ($subject_array["id"] == $subject["id"] || $page_array["subject_id"] == $subject["id"]) {				$page_set=find_pages_for_subject($subject["id"]);		$output .= "<ul class=\"pages\">";		while ($page = mysqli_fetch_assoc($page_set)) {			$output .= "<li ";			if ($page_array && $page["id"] == $page_array["id"]) {			$output .= "class=\"selected\"";		}		$output .= ">";		$output .= "<a href=\"index.php?page=";		$output .= urldecode($page["id"]);		$output .= "\">";		$output .= htmlentities($page["menu_name"]);		$output .= "</a></li> ";	}		$output .= "</ul>" ;		mysqli_free_result($page_set);	}		// End of the subject li		$output .= "</li>";	}		mysqli_free_result($subject_set);		$output .= "</ul>";		return $output;	}	function password_encrypt($password){		$hash_format = "$2y$10$";	 // Tells PHP to use Blowfish with a "cost" Of 10		$salt_length = 22; 			 // blowfish salts should be 22- characters or more		$salt= generate_salt($salt_length);		$format_and_salt = $hash_format.$salt;		$hash= crypt($password , $format_and_salt);		return $hash;	}	function generate_salt($length){		// Not 100% unique, not 100% rendom, but good enough for salt		// MD5 return 32 characters		$unique_random_string= MD5(uniqid(mt_rand(), true));		// valid characters for a salt are [a-zA-Z0-9./]		$base64_string=base64_encode($unique_random_string);		// But not '+' which is valid in base 64 encoding		$modified_base64_string=str_replace('+', '.', $base64_string);		//truncate string to the correct length		$salt=substr($modified_base64_string, 0, $length);		return $salt;	}	function password_check($password, $existing_hash){		$hash = crypt($password, $existing_hash);		if ($hash === $existing_hash) {			return true;		} else{			return false;		}	}	function attempt_login($username, $password){		$admin = find_admin_by_user($username);		if ($admin) {			// found admin, now check password			if (password_check($password, $admin["hashed_password"])) {				// password matches				return $admin;			} else {				// password does not match.				return false;			}		} else{			// admin not found			return false;		}	}	function logged_in(){		return isset($_SESSION['admin_id']);	}	function confirm_logged_in(){		if (!logged_in()) {	redirect_to("login.php");}	}